  useNuiEvent("setHideCoin", setHideCoin);
  useNuiEvent("setHideInfo", setHideInfo);

  useNuiEvent("setName", (name) => {
    setServerInfoDetail({
      ...serverInfoDetail,
      serverName: name,
    });
  });

  useNuiEvent("setCoin", (coin) => {
    setServerInfoDetail({
      ...serverInfoDetail,
      coin,
    });
  });

  useNuiEvent("setBlackMoney", (money) => {
    setServerInfoDetail({
      ...serverInfoDetail,
      darkMoney: money,
    });
  });

  useNuiEvent("setServerLink", (link) => {
    setServerInfoDetail({
      ...serverInfoDetail,
      serverLink: link,
    });
  });

  useNuiEvent("setTime", (time) => {
    setServerInfoDetail({
      ...serverInfoDetail,
      time,
    });
  });

