export const StatusStyle1 = ({ status }) => {
  const { settings } = useSettings();
  const maxStatusValue = 100;

  const healthHeight = `${(status.health / maxStatusValue) * 100}%`;
  const hydrationHeight = `${(status.thirst / maxStatusValue) * 100}%`;
  const hungryHeight = `${(status.hunger / maxStatusValue) * 100}%`;
  const energyHeight = `${(status.energy / maxStatusValue) * 100}%`;
  const armorHeight = `${(status.armor / maxStatusValue) * 100}%`;
  const stressHeight = `${(status.stress / maxStatusValue) * 100}%`;
  const oxygenHeight = `${(status.oxygen / maxStatusValue) * 100}%`;

  return (
    <div className="flex flex-col gap-y-4 w-[590px]">
      <div className="flex items-center w-full justify-between">
        {!settings.status.health ? (
          status.health >
            settings.styleVisibility["1"]?.visibilityThresholds?.health && (
            <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
              <div
                className="bg-[#E12E59] w-full absolute bottom-0"
                style={{ height: healthHeight }}
              ></div>
              <HealthIcon />
            </div>
          )
        ) : (
          <></>
        )}

        {!settings.status.thirst ? (
          status.thirst >
            settings.styleVisibility["1"]?.visibilityThresholds.thirst && (
            <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
              <div
                className="bg-purple-600 w-full absolute bottom-0"
                style={{ height: hydrationHeight }}
              ></div>
              <HydrationIcon />
            </div>
          )
        ) : (
          <></>
        )}

        {!settings.status.hunger ? (
          status.hunger >
            settings.styleVisibility["1"]?.visibilityThresholds?.hunger && (
            <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
              <div
                className="bg-[#DE2EE1] w-full absolute bottom-0"
                style={{ height: hungryHeight }}
              ></div>
              <HungryIcon />
            </div>
          )
        ) : (
          <></>
        )}
        {!settings.status.energy ? (
          status.energy >
            settings.styleVisibility["1"]?.visibilityThresholds?.energy && (
            <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
              <div
                className="bg-[#E12E39] w-full absolute bottom-0"
                style={{ height: energyHeight }}
              ></div>
              <EnergyIcon />
            </div>
          )
        ) : (
          <></>
        )}
        {!settings.status.stress ? (
          status.stress >
            settings.styleVisibility["1"]?.visibilityThresholds?.stress && (
            <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
              <div
                className="bg-[#E12E39] w-full absolute bottom-0"
                style={{ height: stressHeight }}
              ></div>
              <StressIcon />
            </div>
          )
        ) : (
          <></>
        )}

        <div className="w-20 relative h-20 bg-[#010100] flex items-center justify-center">
          <div
            className="bg-[#2EE1B6] w-full absolute bottom-0"
            style={{ height: oxygenHeight }}
          ></div>
          <OxygenIcon />
        </div>
      </div>

      <div className="flex gap-x-2">
        {!settings.status.health ? (
          status.health >
            settings.styleVisibility["1"]?.visibilityThresholds?.health && (
            <div className="w-72 h-12 bg-[#010100] relative flex items-center justify-center">
              <div
                className="h-12 absolute left-0 bg-[#1CB459] flex items-center justify-center"
                style={{ width: healthHeight }}
              ></div>
              <HealthIcon />
            </div>
          )
        ) : (
          <></>
        )}

        {!settings.status.armor ? (
          status.armor >
            settings.styleVisibility["1"]?.visibilityThresholds?.armor && (
            <div className="w-72 h-12 bg-[#010100] relative flex items-center justify-center">
              <div
                className="h-12 absolute left-0 bg-[#2E60E1] flex items-center justify-center"
                style={{ width: armorHeight }}
              ></div>
              <ArmorIcon />
            </div>
          )
        ) : (
          <></>
        )}
      </div>
    </div>
  );
};
